mapper.select:
select id, member_id, article_id, child_id, content, created_at, updated_at, created_by, updated_by from comment
and article_id=#{articleId}
order by
${order.property} ${order.direction}
limit #{pageable.pageSize} offset #{pageable.offset};

mapper.update:
update comment
set content    = #{content},
    updated_at = #{updatedAt},
    updated_by = #{updatedBy}
where id = #{id};
